            if (H5Gclose(subgroup_id) < 0) {
                H5_FAILED();
                printf("    failed to close group '%s'\n", GET_LINK_VAL_TEST_SUBGROUP2_NAME);
                PART_ERROR(H5Lget_val_external);
            }

            PASSED();
